import React from "react";

const Box = (props) => {
  let result;
  if (
    props.title === "컴퓨터" &&
    props.result !== "" &&
    props.result !== "TIE"
  ) {
    result = props.result == "WIN" ? "LOSE" : "WIN";
  } else {
    result = props.result;
  }

  if(result=== "WIN"){
    element.classList.remove('box-deco')
    element.classList.add('win-box')
  }else if (result === "LOSE"){
    element.classList.remove('box-deco')
    element.classList.add('lose-box')
  }


  return (
    <div className={`box-deco ${result}`}>
      <h3>{props.title}</h3>
      <img
        className="img-size"
        alt="img"
        src={props.item && props.item.img}
      ></img>
      <h3>{result}</h3>
    </div>
  );
};

export default Box;
