import { useState } from "react";

import "./App.css";
import Box from "./component/Box";

const choice = {
  rock: {
    name: "Rock",
    img: "https://nationaltoday.com/wp-content/uploads/2021/08/National-Pet-Rock-Day-1200x834.jpg",
  },

  ss: {
    name: "Ss",
    img: "https://cafe24.poxo.com/ec01/bibon1/jy78sT5iv9X+V5IHQBaKwrjoUZw7/nGXjx58SNFF4bK6NrHp9v6yW73e7/DyTNtncYBJmFI45IbmtzNo91A3rQ==/_/web/product/big/202101/833fa88eae073a583699cc737f0b8a91.jpg",
  },
  paper: {
    name: "Paper",
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQfAj6mQwx4OrtR0e6cO9VGfDHSBN900tkOaw&usqp=CAU",
  },
};

function App() {
  const [pick, setPick] = useState(null);
  const [comPick, setComPick] = useState(null);
  const [result, setResult] = useState("");

  const play = (userSelect) => {
    setPick(choice[userSelect]);

    setComPick(comSelect());

    setResult(whatResult(choice[userSelect], comSelect()));
  };

  const whatResult = (a, b) => {
    if (a.name == b.name) {
      return "tie";
    } else if (a.name == "Rock") return b.name == "Ss" ? "WIN" : "lose"
    else if (a.name == "Ss") return b.name == "Paper" ? "WIN" : "lose"
    else if (a.name == "Paper") return b.name == "Rock" ? "WIN" : "lose"
  };

  const comSelect = () => {
    let arrayGame = Object.keys(choice); //뒤에 쩜 네임은 붙이지 않는다.
    let randomNum = Math.floor(Math.random() * arrayGame.length);
    let final = arrayGame[randomNum]; //여기를 빼먹었음
    return choice[final]; //여기도빼먹음
  };


return (
  <div className="main">
    <div className="box-two">
  <Box title={"컴퓨터"} item={comPick} result={result}/>
  <Box title={"나"} item={pick} result={result}/>
  </div>

  <button onClick={()=>play("scissors")}>가위</button>
  <button onClick={()=>play("rock")}>바위</button>
  <button onClick={()=>play("paper")}>보</button>
  </div>
)
}


export default App;
