import { useState } from "react";
import "./App.css";
import Box from "./component/Box";

const choice = {
rock :{
  name:"Rock",
  img :"https://nationaltoday.com/wp-content/uploads/2021/08/National-Pet-Rock-Day-1200x834.jpg"
},
scissors :{
  name:"Scissors",
  img :"https://cafe24.poxo.com/ec01/bibon1/jy78sT5iv9X+V5IHQBaKwrjoUZw7/nGXjx58SNFF4bK6NrHp9v6yW73e7/DyTNtncYBJmFI45IbmtzNo91A3rQ==/_/web/product/big/202101/833fa88eae073a583699cc737f0b8a91.jpg"

},
paper:{
  name:"Paper",
  img :"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQfAj6mQwx4OrtR0e6cO9VGfDHSBN900tkOaw&usqp=CAU"
}
}

function App() {

const[userSelect, setUserSelect] = useState(null)
const[comSelect, setComSelect] = useState(null)

let play=(pick)=>{
  setUserSelect(choice[pick]) 
  setComSelect(computerPick())

}

const computerPick=()=>{
  let arrayName = object.keys(choice)
  let randomNum = Math.floor(Math.random()*arrayName.length)
  let final = arrayName[randomNum]
  return choice[final]
}

return (
  <div className="main"><div className="box-two">
  <Box title={"컴퓨터"} item={comSelect}/>
  <Box title={"나"} item={userSelect}/>
  </div>

  <button onClick={()=>play("scissors")}>가위</button>
  <button onClick={()=>play("rock")}>바위</button>
  <button onClick={()=>play("paper")}>보</button>
  </div>
)
}


export default App;
